@import url("https://fonts.googleapis.com/css2?family=Roboto+Mono:ital,wght@0,500;0,700;1,400&display=swap");
@import "./loader.scss";
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}
html {
  font-size: 62.5%;
  font-family: "Roboto Mono", monospace;
}
body {
  font-size: 16px;
  background: #34495e;
  min-height: 100vh;
  width: 100vw;
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 20px;
}
.container {
  &__title {
    color: white;
    text-align: center;
    justify-content: space-between;
  }
  &__characters {
    margin-top: 20px;
    width: 90vw;
    display: flex;
    flex-wrap: wrap;
  }
  &__character {
    flex-basis: 32.8%;
    background: #c8d6e5;
    border-radius: 1rem;
    overflow: hidden;
    cursor: pointer;
    padding: 1.5rem;
    margin-bottom: 1rem;
    transition: transform 0.2s;

    &:hover {
      transform: scale(1.05);
      box-shadow: 0 0 1rem #8395a7;
    }

    &-image {
      display: block;
      margin: 1rem auto;
      width: 40%;
      height: 70%;
    }
    & p {
      & span {
        font-weight: 700;
      }
      margin-bottom: 1rem;
    }
  }
}
